const CANVASX = 420;
const CANVASY = 320;
// needs to stay at 7 to match the size of the fft
const SCALE = 7;
const SAMPLE = 15;
const WINDOW = 6;

let capture;
let debug = true;
let mic, fft;

function setup() {
  rectMode(CENTER);
  createCanvas(CANVASX, CANVASY);
  capture = createCapture(VIDEO);
  capture.size(CANVASX / SCALE, CANVASY / SCALE);
  // get the microphone input
  mic = new p5.AudioIn();
  mic.start();
  // instantiate an fft of the microphone
  fft = new p5.FFT();
  fft.setInput(mic);
  // hide the mini image
  capture.hide();
}

function draw() {
  background(51);

  // get the fft of the microphone input
  let spectrum = fft.analyze();  

  // map the input specturm to the output image 
  let mapOutput = mapSpectrum(spectrum);
  let scale = mapOutput[0];
  let doubleSpectrum = mapOutput[1];  

  // draw the mirrored spectrum on the botom of the canvas
  drawDebug(doubleSpectrum);

  // draw the final image
  drawImage(scale, doubleSpectrum);
}


// draw the image
function drawImage(scale, spectrum){
  push();

  capture.loadPixels();

  // create the bound for a window in the center of the image
  let xStart = round(capture.width / WINDOW); 
  let xStop  = round((WINDOW -1) * capture.width / WINDOW); 
  let yStart = round(capture.height / WINDOW); 
  let yStop  = round((WINDOW - 1) * capture.height / WINDOW); 

  // draw the pixels
  for(let i = yStart; i < yStop; i++){
    for(let j = xStart; j < xStop; j++){
       let index = (j + i * capture.width) * 4;
       let r = capture.pixels[index];
       let g = capture.pixels[index + 1];
       let b = capture.pixels[index + 2];
       let a = capture.pixels[index + 3];
       noStroke();
       fill(spectrum[i], g, b);
    //    fill(scale, 100, 100);
       let w = scale[j + i * capture.width];
       rect(j * SCALE, i * SCALE, w, w); 
       // rect(j * SCALE, i * SCALE, 6, 6); 
    }
  }

  pop();
}
// remap the fft spectrum
function mapSpectrum(spectrum){
  let scale = [];
  let color = [];
  let doubleSpectrum = [];  

  // mirror the fft into an array twice as large
  for(let i = 0; i < spectrum.length * 2; i++){
    if(i < spectrum.length){
      doubleSpectrum[i] = spectrum[spectrum.length - 1 - i];
    }else{
      doubleSpectrum[i] = spectrum[i - spectrum.length]; 
    }
    // map the spectrum into the size of a pixel
    scale[i] = map(doubleSpectrum[i], 0, 255, 0, 3 * SCALE);
    // color[i] = doubleSpectrum[i];
  } 

  return [scale, doubleSpectrum];
}
// draw the raw fft on the bottom of the screen
function drawDebug(spectrum){
  push();
  colorMode(HSB);
  if(debug){
    noStroke();
    for (let i = 0; i < spectrum.length; i++){
      let x = map(i, 0, spectrum.length, 0, width);
      let h = map(spectrum[i], 0, 255, 0, 50);
      let hue = map(spectrum[i], 0, 255, 0, 360);
      fill(hue, 100, 100);
      rect(x, height, width / spectrum.length, -h);
    }
    // center point
    fill('black');
    rect(width / 2, height, 1, -10);
  }
  pop(); 
}

