function randomInt(min, max) {
  return Math.floor(Math.random() * (max - min) + min);
}

function sample(data) {
  const index = Math.floor(Math.random(data.length));
  return data[index];
}

function constrain(v, min, max) {
  return Math.min(max, Math.max(min, v));
}

