function checkCell(x, y, direction){
  if(y > 0 && y < grid_rows - 1){
   if (direction == "up") {
     if (!grid[x - 1][y]     && !grid[x][y]     && !grid[x + 1][y] &&
         !grid[x - 1][y - 1] && !grid[x][y - 1] && !grid[x + 1][y - 1]) {
       console.log("up true");
       return true;
     }
   } else if (direction == "down") {
     if (!grid[x - 1][y]     && !grid[x][y]     && !grid[x + 1][y] && 
         !grid[x - 1][y + 1] && !grid[x][y + 1] && !grid[x + 1][y + 1]){ 
       console.log("down true");
       return true;
     }
   }
  }

  if(x > 0 && x < grid_cols - 1){
    if (direction == "right") {
      if(!grid[x][y - 1]     && !grid[x][y]         && !grid[x][y + 1] && 
         !grid[x + 1][y - 1] && !grid[x + 1][y] && !grid[x + 1][y + 1]){
         console.log("right true");
         return true;
      }
    } else if (direction == "left") {
      if(!grid[x][y - 1]     && !grid[x][y]         && !grid[x][y + 1] && 
         !grid[x - 1][y - 1] && !grid[x - 1][y] && !grid[x - 1][y + 1]){
         console.log("left true");
        return true;
      }
    }
  }

  return false;
}

