    if((c[0] > 200 && c[1] > 100 && c[2] > 200)){
      c[0] = 0;
      c[1] = 0;
      c[2] = 0;
    }else if(c[0] < 10 && c[1] < 10 && c[2] < 10){
      c[0] = random(100, 255);
      c[1] = random(50, 100);
      c[2] = random(100, 200);
    }else if(c[0] > 100 && c[0] < 200){
      c[0] = 0;
      c[1] = 255 - c[1]; // random(255);
      c[2] = 0;
    }else if(c[0] < 10 && c[2] < 10 && c[1] > 200){
      c[2] = random(100, 200);
    }
    setQuick(cabbage, i % cabbage.width, floor(i / cabbage.width), c);
  }
}

function getQuick(img, x, y) {
  const i = (y * img.width + x) * 4;
  return [
    img.pixels[i],
    img.pixels[i + 1],
    img.pixels[i + 2],
    img.pixels[i + 3],
  ];
}

function setQuick(img, x, y, c) {
  const i = (y * img.width + x) * 4;

  img.pixels[i + 0] = c[0];
  img.pixels[i + 1] = c[1];
  img.pixels[i + 2] = c[2];
  img.pixels[i + 3] = 255; // c[3];
}

