const palette = ['#f94144', '#f3722c', '#f8961e', '#f9844a', '#f9c74f', '#90be6d', '#43aa8b', '#4d908e', '#577590', '#277da1'];

function setup() {
  createCanvas(600, 600);
  pixelDensity(1);
  noSmooth();
  noFill();
  strokeWeight(0.5);

  noLoop();
}

function draw() {
  background(0);

  loadPixels();
  for(let i = 0; i < 150; i++) {
    let index = floor(random(width * height));
    let size = floor(random(2, 10));
    let col = color(palette[floor(random(palette.length))]);
    createCross(index, size, col)
  }
  updatePixels();
}

function createCross(i, size, c) {
  let w = width, h = height;
  // horizontal
  for(let col = floor(i / w) * w; col < ceil(i / w) * w; col++) {
    let offset = round(map(noise(col * 0.02), 0, 1, -20, 20));
    for(let s = 0; s < size; s++) {
      pixels[(col + (s + offset) * w) * 4] = red(c);
      pixels[(col + (s + offset) * w) * 4 + 1] = green(c);
      pixels[(col + (s + offset) * w) * 4 + 2] = blue(c);
    }
  }
  // verticle
  for(let row = i % w; row < i % w + w * (h - 1); row += w) {
    let offset = round(map(noise(row * 0.02), 0, 1, -20, 20));
    for(let s = 0; s < size; s++) {
      pixels[(row + s + offset) * 4] = red(c);
      pixels[(row + s + offset) * 4 + 1] = green(c);
      pixels[(row + s + offset) * 4 + 2] = blue(c);
    }
  }
}

