module cut_tun (pos, h, r){
    translate(pos+[0,0,h-1]){
        rotate(90, [1,0,0]){
            linear_extrude(height = 60, center = true){
                union(){
                    circle(r, $fn=500, true);
                    translate([0,-h1/2,0]){
                        square([r*2, h], true);
                    }
                }
            }
        }
    } 
}

module cut_arch(h ,r){
