module cut(r){
        translate([r-0.07*r, r-0.07*r, 0])
            cylinder(100, r, r, $fn=500, true);
}


module pad(pos, size, r, h){

    translate(pos+[0,0,h]){
        translate([0,0,-h]){
            difference() {
                cube([size, size, 0.5],true); // Outer
                cut_edge(r);
            }


            translate([0, 0, 0.5])
                difference() {
                    cube([size-2, size-2, 0.5], true); // Padding
                    cut_edge(r-0.04*r);
                }
            translate([0, 0, h/2])
                difference() {
                    cube([size-4, size-4, h], true); // Inner
                    cut_edge(r-0.08*r);
                }
            }
        mirror([0, 0, 1]){
            translate([0,0,-3]){
            difference() {
                cube([size, size, 0.5],true); // Outer
                cut_edge(r);
            }


            translate([0, 0, 0.5])
                difference() {
                    cube([size-2, size-2, 0.5], true); // Padding
                    cut_edge(r-0.04*r);
                }
            translate([0, 0, h/2])
                difference() {
                    cube([size-4, size-4, h], true); // Inner
                    cut_edge(r-0.08*r);
                }
            }
        }
    } 
} 

module cut_edge(r){
cut(r);
mirror([1,1,0]) cut(r);
mirror([1,0,0]) cut(r);
mirror([0,1,0]) cut(r);
}

