// warp the little thing with noise, closed, smooth

function touchUp(p) {
    for (i = 0; i < p.segments.length; i++) {
        var s = p.segments[i];
        var f = 0.001;
        var x = s.point.x * f;
        var y = s.point.y * f;
        s.point.x += noise(x, y) * 20;
        // s.point.y += noise(x + 5, y) * 30;
        s.point.y += noise(x + i * 0.001, y) * 20;
    }
    p.closed = true;
    p.smooth();
}

