// save the branching pattern to some kind of set or array
// draw it with different widths to make it look like its pulsing


function Branch(depth){
  this.initialDepth = depth;
  this.depth = depth;
  this.branchCount = 0;
  this.branchRotations = [];
  this.wiggleRotations = [];
}

Branch.prototype.resetDepth = function(){
  this.depth = this.initialDepth;
  this.branchCount = 0;
}

Branch.prototype.setupBranch = function(depth){
  var self = this;
  console.log(depth, self.depth, self.branchCount);

  push();
  if(depth < 10){  
    { 
      translate(0,-height/LINE_SCALE);

      let wR = random(-WIGGLE, WIGGLE);
      self.wiggleRotations.push(wR); 
      rotate(wR);  

      let bR = random(1.0);
      self.branchRotations.push(bR);
      console.log(bR);

      self.branchCount++;
      if(bR < BRANCH_PROB){    
        rotate(ROTATION); 
        scale(0.8); 
        push(); 
        self.setupBranch(depth + 1); 
        pop(); 
        rotate(-2 * ROTATION); 
        push();
        self.setupBranch(depth + 1);    
        pop();       
      }else{ 
        self.setupBranch(depth);
      } 
    } 
  }
  console.log("out here");
  pop();
}

Branch.prototype.draw = function(depth){
  var self = this;
  console.log(self.depth, self.branchCount);
  push();
  if(depth < 10){ 
    line(0,0,0,-height/LINE_SCALE); 
    { 
      translate(0,-height/LINE_SCALE);
      let wR = self.wiggleRotations[this.branchCount]; 
      rotate(wR);  

      let bR = self.branchRotations[this.branchCount];
      if(bR < BRANCH_PROB){
        console.log("here");
        self.branchCount++;    
        rotate(ROTATION); 
        scale(0.8); 

        push(); 
        // self.depth = self.depth + 1;
        self.draw(depth + 1); 
        pop(); 

        rotate(-2 * ROTATION); 

        push();
        // self.depth = self.depth + 1;
        self.draw(depth + 1);    
        pop();       
      }else{ 
        self.branchCount++;
        self.draw(depth + 1);
      } 
    } 
  }
  pop();
} 

let branchCount = 0;
function dumbBranch(depth){
  push();
  console.log(depth, branchCount);
  if(depth < 10){ 
    line(0,0,0,-height/LINE_SCALE); 
    { 
      translate(0,-height/LINE_SCALE);
      rotate(random(-WIGGLE, WIGGLE));

      if(random(1.0) < BRANCH_PROB){
        branchCount++;
        rotate(ROTATION); 
        scale(0.8); 
        push(); 
        dumbBranch(depth + 1); 
        pop();  
        rotate(-2 * ROTATION); 
        push();
        dumbBranch(depth + 1);    
        pop();       
      }else{ 
        branchCount++;
        dumbBranch(depth);
      } 
    } 
  }
  pop();
}

