function frame(x,y){
translate(width/2,height/2);
// stroke(255, 0, 0);
fill(0,0,0);
beginShape();
// Exterior part of shape, clockwise winding
vertex(-x, -x);
vertex(x, -x);
vertex(x, x);
vertex(-x, x);
// Interior part of shape, counter-clockwise winding
beginContour();
vertex(-y, -y);
vertex(-y, y);
vertex(y, y);
vertex(y, -y);
endContour();
endShape(CLOSE);
}

