const debug = false;
const LINEWIDTH = 40;
const POINTS = 10;
const RADIUS = 200;
const RADIAL = false;
const CLOSE = true;
const curvePoints = new Set();

function setup() {
  createCanvas(800, 800);
  noLoop();
};

function draw() {
  background(250);
  strokeWeight(LINEWIDTH);
  blendMode(MULTIPLY);

  // if radial mode, then go to the center of the page
  if(RADIAL){
    translate(height / 2, width / 2);
  }
  // do stuff                                                          
  drawSquiggles(getPoints());
};




function getPoints(){
  let x1;
  let y1;
  if(RADIAL){
    // start points are the 0 angle
    x1 = RADIUS * cos(0);
    y1 = RADIUS * sin(0);
    // fill up the set with a bunch of points around a circle
    for(let i = 0; i < 2 * PI; i += 2 * PI / POINTS){
      curvePoints.add({x2: RADIUS * cos(i),
                       y2: RADIUS * sin(i),
                       xA: random(0, width),
                       yA: random(0, height),
                       xB: random(0, width),
                       yB: random(0, height)});
    console.log(curvePoints);
    }
  } else {
    // fill up the set with a bunch of random points
    for(let i = 0; i < POINTS; i++){
      // start points are also random
      x1 = random(0, width);
      y1 = random(0, height);
      // TODO prevent this from picking same point twice
      // create a set of control points and end points, add to set
      curvePoints.add({x2: random(0, width),
                       y2: random(0, height),
                       xA: random(0, width),
                       yA: random(0, height),
                       xB: random(0, width),
                       yB: random(0, height)});
    }
  }

  return[x1, y1];
}

// draw the squiggles
function drawSquiggles(startPoints){
  // save this point so we can close the shape at the end
  let x1 = startPoints[0];
  let y1 = startPoints[1];
  let x1I = x1;
  let y1I = y1;

  // not draw lines from the points 
  for(let i = 0; i < POINTS; i++){
    push();

    // increase the stroke
    // stroke(i * 50);
    stroke(random(0, 255), random(0, 255),random(0, 255));

    if(RADIAL){
      circle((LINEWIDTH * i * 1.5) - width / 2 + LINEWIDTH, -height / 2 + LINEWIDTH, LINEWIDTH / 4);
    } else {
      circle((LINEWIDTH * i * 1.5) + LINEWIDTH, LINEWIDTH, LINEWIDTH / 4);
    }

    // this rest notation is pretty cool...
    // pick a point from the set
    const point = pick([...curvePoints]);

    // draw the curve
    curve(point.xA, point.yA, x1, y1, point.x2, point.y2, point.xB, point.yB);

    // assign the start of the next curve to be the end of the prior
    x1 = point.x2;
    y1 = point.y2;

    // draw the last segment to connect the whole thing
    if(i == POINTS - 1 && CLOSE){
      curve(point.xA, point.yA, x1, y1, x1I, y1I, point.xB, point.yB);
    }

    // debug
    if(debug){
      console.log(point.xA, point.yA, x1, y1, point.x2, point.y2, point.xB, point.yB);
      console.log(x1, y1);
    }

    // remove the used point from the set
    curvePoints.delete(point);
    pop();
  }
}


function markerLine(x1, y1, x2, y2) {
  // add some randomness to the end points of the line
  x1 += j();
  y1 += j();
  x2 += j();
  y2 += j();

  // create randomized control points for the line
  // it will be a curve, but just barely
  // this code places the control point nearly along the line 
  // within +/- 20 of the endpoints of the line
  const xA = lerp(x1, x2, -0.5) + random(-20, 20);
  const yA = lerp(y1, y2, -0.5) + random(-20, 20);
  const xB = lerp(x1, x2, -0.5) + random(-20, 20);
  const yB = lerp(y1, y2, -0.5) + random(-20, 20);

  push();

  // choose strokeweight
  strokeWeight(LINEWIDTH + random(0, 2));

  // blue line with variable opacity 
  stroke(70, 70, 220, random(200, 255)); // blue marker

  // the actual marker line with control points
  // console.log(xA, yA, x1, y1, x2, y2, xB, yB);
  curve(xA, yA, x1, y1, x2, y2, xB, yB);

  // create a bleed effect at the beginning and end of the line 
  // the start bleed is small while the end bleed is large
  const heavyStart = random(0.0, 0.1);
  const heavyEnd = random(0.9, 1.0);
  // 25% chance of each condition
  if (random() < 0.75)
    // draw a line from the start a point pretty close to the beginning
    line(x1, y1, lerp(x1, x2, heavyStart), lerp(y1, y2, heavyStart));
  if (random() < 0.75)
    // draw a line from the end to a point slightly firthe from the end
    line(lerp(x1, x2, heavyEnd), lerp(y1, y2, heavyEnd), x2, y2);
  pop();
}

// returns a random number up to the random element in the array
function j() {
  return random(pick([0, 0, 0, 0, -0.5, -0.5, 0.5, 0.5, -1, 1]));
}

// returns a random element in the array
function pick(arr) {
  return arr[floor(random(arr.length))];
}

// random integer value rounded down
function randomInt(max) {
  return floor(random(max));
}

