var img;

function preload() {
  img = loadImage("myavatar.jpg");
}
function setup() {
  createCanvas(600, 600);
  noSmooth();
  noLoop();
}

function draw() {
  background(0);

  let w = img.width, h = img.height;
  img.loadPixels();
  for(let row = 0; row < h; row++) {
    let R = [], G = [], B = [];
    for(let col = 0; col < w; col++) {
      R.push(img.pixels[(row * w + col) * 4]);
      G.push(img.pixels[(row * w + col) * 4 + 1]);
      B.push(img.pixels[(row * w + col) * 4 + 2]);
    }
    let offset = round(map(noise(row * 0.02), 0, 1, w / -5, w / 5));
    for(let i = 0; i < w; i++) {
      let alpha = map(noise(i * 0.005, row * 0.1), 0, 1, 120, 255);
      img.pixels[(row * w + i) * 4] = R[abs(i + offset) % R.length];
      img.pixels[(row * w + i) * 4 + 1] = G[abs(i + offset) % G.length];
      img.pixels[(row * w + i) * 4 + 2] = B[abs(i + offset) % B.length];
      img.pixels[(row * w + i) * 4 + 3] = alpha;
    }
  }
  img.updatePixels();
  image(img, 0, 0, width, height);
}

