const canvasSketch = require("canvas-sketch");
const random = require('canvas-sketch-util/random');
const palettes = require('nice-color-palettes');
const eases = require('eases');
const BezierEasing = require('bezier-easing');

// Ensure ThreeJS is in global scope for the 'examples/'
global.THREE = require("three");

// Include any additional ThreeJS examples below
require("three/examples/js/controls/OrbitControls");

const settings = {
  dimensions: [512, 512],
  fps: 24,
  duration: 6,
  // Make the loop animated
  animate: true,
  // Get a WebGL canvas rather than 2D
  context: "webgl",
  // Gives a soft edge
  attributes: { antialias: true }
};

const sketch = ({ context }) => {
  // Create a renderer
  const renderer = new THREE.WebGLRenderer({
    context
  });

  // WebGL background color
  renderer.setClearColor("white", 1);
  //setting alpha to 0 would make a transparent image

  // Setup a camera
  const camera = new THREE.OrthographicCamera();


  // Setup your scene
  const scene = new THREE.Scene();

  const palette = random.pick(palettes);

  // Setup a mesh with geometry + material
  const box = new THREE.BoxGeometry(1, 1, 1);

  for (let i = 0; i < 40; i++) {
    const mesh = new THREE.Mesh(
      box,
      //MeshBasicMaterial does not reflect light
      new THREE.MeshStandardMaterial({
        color: random.pick(palette),
        roughness: 0.75,
        flatShading: true

      })
    );
    mesh.position.set(
      random.range(-1, 1),
      random.range(-1, 1),
      random.range(-1, 1)
    );

    mesh.scale.set(
      random.range(-1, 1),
      random.range(-1, 1),
      random.range(-1, 1)
    );

    mesh.scale.multiplyScalar(0.5);
    scene.add(mesh);
  }

  scene.add(new THREE.AmbientLight('hsl(0,0%,20%)'))

  const light = new THREE.DirectionalLight('white', 1);
  light.position.set(0, 0, 4);

  scene.add(light);
  //intersecting trys to resemble real life

  //custom easing
  const easeFn = BezierEasing(0.67, 0.03, 0.29, 0.99);

  // draw each frame
  return {
    // Handle resize events here
    resize({ pixelRatio, viewportWidth, viewportHeight }) {
      renderer.setPixelRatio(pixelRatio);
      renderer.setSize(viewportWidth, viewportHeight, false);

      const aspect = viewportWidth / viewportHeight;

      // Ortho zoom
      const zoom = 1.0;

      // Bounds
      camera.left = -zoom * aspect;
      camera.right = zoom * aspect;
      camera.top = zoom;
      camera.bottom = -zoom;

      // Near/Far
      camera.near = -100;
      camera.far = 100;

      // Set position & look at world center
      camera.position.set(zoom, zoom, zoom);
      camera.lookAt(new THREE.Vector3());

      // Update the camera
      camera.updateProjectionMatrix();

    },
    // Update & render your scene here
    //playhead works when there is a specified duration
    render({ playhead }) {
      const t = Math.sin(playhead * Math.PI);

      scene.rotation.z = easeFn(t);
      // scene.rotation.z = eases.expoInOut(t);
      // mesh.rotation.y = time * (10 * Math.PI / 180);
      // scene.rotation.x = playhead * Math.PI * 2;
      // scene.rotation.x = Math.sin(playhead * Math.PI * 2);
      renderer.render(scene, camera);
    },
    // Dispose of events & renderer for cleaner hot-reloading
    unload() {
      renderer.dispose();
    }
  };
};

canvasSketch(sketch, settings);

