let t;

function setup() {
  createCanvas(600, 600);
  colorMode(HSB);

  stroke(0, 0, 100);

  t = new Turtle();
}

function draw() {
  background(0);
  translate(-30, 0);
  scale(1.1)
  t.penUp();
  t.turnTo(-90);

  // red spirograph
  drawSpiro1(425, 325);

  // blue spirographs
  drawSpiro2(340, 180, 150, 12, 80);
  drawSpiro2(90, 300, 182, 80, 100);
  drawSpiro2(430, 500, 156, 30, 130);

  // green spirographs
  drawSpiro3(200, 450, 190, 30, 80);
  drawSpiro3(480, 130, -185, 30, 80);
  drawSpiro3(250, 150, 226, 30, 90);

  //draws red snake :)
  drawSnake(290, 300, 2000);

}

function drawSpiro1(x, y){
  push();
  stroke(0, 100, 100);
  t.pushState();
  t.moveTo(x, y);
  t.penDown();
  for(let i = 0; i < 18; i++){
    t.moveForward(100);
    t.turnRight(100);
  }
  t.penUp();
  t.popState();
  pop();
}

function drawSpiro2(x, y, angle, num, length){  
  push();
  stroke(230, 100, 100);
  t.pushState();
  t.moveTo(x, y);
  t.penDown();
  for(let i = 0; i < num; i++){
    t.moveForward(length);
    t.turnRight(angle);
  }
  t.penUp();
  t.popState();
  pop();
}


function drawSpiro3(x, y, angle, num, length){  
  push();
  stroke(150, 100, 100);
  t.pushState();
  t.moveTo(x, y);
  t.penDown();
  for(let i = 0; i < num; i++){
    t.moveForward(length);
    t.turnRight(angle);
    t.moveForward(length/5);
    t.turnRight(angle/5);
  }
  t.penUp();
  t.popState();
  pop();
}

function drawSnake(x, y, length){
  push();

  t.pushState();
  t.moveTo(x, y);
  t.penDown();
  for(let i = 0; i < length; i++){
    strokeWeight(map(i, 0, length, 0.25, 6));
    if(i%10){
      stroke(0, 100, 100);
    } else {
      stroke(10, 0, 100);
    }
    t.moveForward((i*2)/length)
    t.turnRight(1.3);
  }
  t.penUp();
  t.popState();
  pop();
}

