//parameters: change here
let speed = 60;
let leftHandRhythm = 8 //left hand beats
let rightHandRhythm = 16 //right hand beats

//Code Below::::::::::::::::::::::::::::

let soundLayers = []

const chordLow = [
  ["A2", "1n"],
  // ["B2", "4n"],
  ["C#3", "1n"],
  // ["D3", "4n"],
  ["E3", "1n"],
  // ["F#3", "4n"],
  // ["G#3", "4n"],
  // ["A3", "4n"],
];

const chord = [
  ["A5", "8n"],
  ["B5", "8n"],
  ["C#5", "4n"],
  ["D4", "2n"],
  ["E4", "2n"],
  ["F#4", "2n"],
  ["G#4", "4n"],
  ["A4", "4n"],

];
function setup() {
  speed = 100/speed
  angleMode(DEGREES)
  textAlign(CENTER, CENTER)
  createCanvas(400, 400);
  noStroke()
  frameRate(60)

  generateStations(rightHandRhythm, 70, chord);
  generateStations(leftHandRhythm, 150, chordLow)
  soundLayers[0].synth.volume.value = -10
}

function draw() {
  background(220);
  soundLayers.forEach(element => {
    element.update()
  });



}

function reassignStations(layer){

  // if(chordLow.length < 13){
  //   chordLow.push([Tone.Frequency(chord[chordLow.length-1][0]).transpose(-12), "4n"])
  // }
  // try {
  //   console.log(chord[chordLow.length-1])
  // } catch (error) {

  // }

  for (let i = 0; i < layer.stations.length; i++) {
    layer.stations[i].note = random(layer.chord)
  }
}

function generateStations(count, size, chord) {

  let v = createVector(0, -100)
  v.setMag(size)
  let ball = new Ball(200, 200, 20)
  let stations = []
  for (let i = 0; i < count; i++) {
    stations.push(new Station(200 + v.x, 200 + v.y, 40))
    v.rotate(-360 / count)
  }

  for (let i = 0; i < stations.length; i++) {
    stations[i].note = random(chord)
    if (i < stations.length - 1) {
      stations[i].next = stations[i + 1]
    } else {
      stations[i].next = stations[0]
    }

  }

  let layer = new soundLayer(ball, stations, count, chord);
  layer.ball.parent = layer;
  layer.ball.start = layer.stations[0]
  layer.ball.target = layer.stations[1]

  layer.stations.forEach(element => {
    element.parent = layer;
  });
  soundLayers.push(layer);
}

class soundLayer {
  constructor(ball, stations, rhy, chord) {
    this.chord = chord
    this.stations = stations;
    this.ball = ball
    this.animatedTarget
    this.rhythm = rhy
    this.startFrame = frameCount
    this.progress = 0
    this.synth = new Tone.Synth().toMaster();

  }

  update() {
    this.progress = ((frameCount - this.startFrame) % (60 * speed)) / (speed*60)//progress from 0 to 1
    this.ball.update();
    this.ball.render();

    this.stations.forEach(element => {
      element.render();
    });

    this.ball.start.animate();
    if (this.animatedTarget != undefined) {
      this.animatedTarget.animate();
    }

  }
}

class Ball {
  constructor(x, y, r) {
    this.x = x
    this.y = y
    this.r = r
    this.parent
    this.currentRhy = 0;
    this.start
    this.target
    var r = random(255); // r is a random number between 0 - 255
    var g = random(100, 200); // g is a random number betwen 100 - 200
    var b = random(200); // b is a random number between 0 - 100
    var a = random(200, 255); // a is a random number between 200 - 255

    this.color = color(r, g, b, a)
  }

  update() {
    // let dirV = this.parent.target.returnPos().sub(this.pos())
    // let dist = dirV.mag()
    // let speed = 4 * this.speed

    let progress = this.parent.progress //progress from 0 to 1

    let rhyCount = (int)(progress / (1 / this.parent.rhythm))

    if (rhyCount != this.currentRhy) {
      if(rhyCount == 0){
        reassignStations(this.parent)
      }
      this.currentRhy = rhyCount
      this.target.play();
      this.start = this.parent.stations[rhyCount]
      this.target = this.start.next
    }


    let start = this.start
    let target = this.target

    let rhyProgress = (progress % (1 / this.parent.rhythm)) * this.parent.rhythm

    this.x = lerp(start.x, target.x, rhyProgress)
    this.y = lerp(start.y, target.y, rhyProgress)

    // this.parent.animatedTarget = this.parent.target;
    // synth.triggerAttackRelease("C4", "8n");
    // this.parent.target.play();
    // this.parent.target = this.parent.target.next;


  }

  pos() {
    return createVector(this.x, this.y);
  }

  render() {
    fill(this.color)
    circle(this.x, this.y, this.r)
  }
}

class Station {
  constructor(x, y, r) {
    this.x = x
    this.y = y
    this.r = r
    this.next;
    this.note;
    this.parent; 

    var r = random(255); // r is a random number between 0 - 255
    var g = random(100, 200); // g is a random number betwen 100 - 200
    var b = random(200); // b is a random number between 0 - 100
    var a = random(200, 255); // a is a random number between 200 - 255

    this.color = color(r, g, b, a)

  }
  returnPos() {
    return createVector(this.x, this.y);
  }

  play() {
    this.parent.synth.triggerAttackRelease(this.note[0], this.note[1]);

  }

  render() {
    if (this.note == undefined) {
      this.note = random(chord)
    }
