let bg
let numSlice = 3
let slice, sum = 0
let split = []
let width = 600
let height = 760
let margin
let covers = [15, 20, 40]

function setup() {
	createCanvas(600, 760);
	bg = [random(150), random(155), random(155)]
	background(bg);

	slice = Math.floor(Math.random() * numSlice) + 2;
	for (let i = 0; i < slice; i++) {
		split[i] = random(10, 100)
		sum += split[i]
	}
	for (let i = 0; i < slice; i++) {
		split[i] /= sum
	}

	margin = random(15, 50)
	height -= margin * 2
	width -= margin
	sum = 0

	for (let i = 0; i < slice; i++) {
		let colorSquare = [random(255), random(255), random(255)]
		if (random(10) > 9) {
			colorSquare = [random(20), random(20), random(20)]
		} else if (random(10) < 2) {
			colorSquare = [random(240, 255), random(240, 255), random(240, 255)]
		}

		noStroke()
		paint(colorSquare, margin, height * sum + margin, width - margin * 2, height * split[i] - margin)
		sum += split[i]
	}
}

function paint(colorSquare, startX, startY, widther, heighter) {
	let endX = startX + widther
	let endY = startY + heighter
	let paintColor = color(colorSquare)
	let paintAlpha
	let edge = random(0.1, 0.9)
	let den = random(covers)

	for (let i = startX; i < endX; i += random(1, 5)) {
		for (let j = startY; j < endY; j += random(1)) {
			if (i < (startX + widther * 0.01) || i > endX * 0.9 || j < (startY + height * 0.01) || j > endY * 0.9) {
				paintAlpha = random(20)
				paintColor.setAlpha(paintAlpha)
				fill(paintColor)
				if (random(3) < 2) {
					rect(i + random(-5, 10), j + random(-5, 5), random(20), random(35))
				} else {
					rect(i + random(-15, 10), j + random(-2, 2), random(30), random(10))
				}
			} else {
				paintAlpha = random(den)
				paintColor.setAlpha(paintAlpha)
				fill(paintColor)
				if (random(3) < 2) {
					rect(i + random(-5, 5), j + random(-5, 5), random(20), random(35))
				} else {
					rect(i + random(-10, 10), j + random(-3, 3), random(20), random(5))
				}
			}
		}
	}
}

