let startGreen;
let startBlue;

let middle = 0; // ...
let circleRadius = 10; // ...
let circles;
let circleNoise = [];

function setup() {
  createCanvas(windowWidth, windowHeight);

  startRed = floor(min(random(40,255), random(40,255), random(40,255), random(40,255)));
  startGreen = floor((random(90,255), random(90,255), random(90,255), random(90,255))/4);
  startBlue = floor((random(100,255), random(100,255), random(100,255), random(100,255))/4);

  shapeRadius = (random(width*0.2, width*0.4) + random(width*0.2, width*0.4) + random(width*0.2, width*0.4) + random(width*0.2, width*0.4) + random(width*0.2, width*0.4) + random(width*0.2, width*0.4) + random(width*0.2, width*0.4) + random(width*0.2, width*0.4))/8;

  circles = floor(random(3,8));

  for (let i = 0; i < circles; i++) {
    circleNoise[i] = noise(random(3,8));
  }

}

function draw() {
  backgroundString = "rgb(" + floor((noise(noiseFactor) * startRed) + 20) + "," + floor((noise(noiseFactor) * startGreen) + 20) + "," + floor( (noise(noiseFactor) * startBlue) + 20) + ")";

  background(backgroundString);
  noiseFactor += 0.01;
  let noiseValue = map(noise(noiseFactor) * easingFactor, 0, 1, 100, 120);

  let incrementer = TWO_PI/noiseValue;

  noStroke();

  translate(width/2, height/2);  


  shadowString = "rgb(" + floor(startRed + (noise(noiseFactor) * startRed)) + "," + floor(startGreen + (noise(noiseFactor) * startGreen)) + "," + floor(startBlue + (noise(noiseFactor) * startBlue)) + ")";

  drawingContext.shadowBlur = 30 + noise(noiseFactor) * 75;
  drawingContext.shadowColor = shadowString;

  planetString = "rgb(" + floor((noise(noiseFactor) * startRed)) + "," + floor((noise(noiseFactor) * startGreen)) + "," + floor( (noise(noiseFactor) * startBlue)) + ")";

  beginShape();
    fill(planetString);
    for(let i = 0; i < TWO_PI; i+= incrementer) {
      let x = cos(i + sin(i * 0.1) * 100) * shapeRadius;
      let y = sin(i + sin(i * 0.1) * 100) * shapeRadius;
      vertex(x, y);
    }
  endShape();


  push();
  let angle = Math.PI*2 / circles;

  rotate(0.0015 * frameCount * 2);

  moonString = "rgb(" + floor((noise(noiseFactor) * startRed) + 15) + "," + floor((noise(noiseFactor) * startGreen) + 15) + "," + floor( (noise(noiseFactor) * startBlue) + 15) + ")";


  for(var i = 0; i < circles; i++){
      fill(moonString);
      xCircle = middle + cos(angle*i) * (shapeRadius * 0.5 + (shapeRadius * circleNoise[i]) + 40 + (circleNoise[i] * 40));
      yCircle = middle - sin(angle*i) * (shapeRadius + 50 + (circleNoise[i] * 80));
      ellipse(xCircle, yCircle, circleRadius*2, circleRadius*2);
  }
  pop();
}

