const inc = 0.01;
const scale = 30;
let cols, rows;
let zoff = 0;

function setup() {
    createCanvas(600, 600);
    cols = floor(width / scale);
    rows = floor(height / scale);
    angleMode(DEGREES);
}

function draw() {
    background(0);

    let yoff = 0;


    for (let y = 1; y <= height; y++) {
        let xoff = 0;

        for (let x = 1; x <= cols; x++) {
            n = noise(x * 0.001, yoff, zoff);
            xoff += n * 9 - 3;
            // push();
            stroke(255);
            strokeWeight(xoff * 0.02);
            strokeCap(SQUARE);
            x1 = (x - 1) * scale + xoff;
            x2 = (x) * scale - xoff;
            y1 = (y - 1) + yoff * 2;
            y2 = y - yoff * 2;

            line(x1, y1, x2, y2);
            // line(x1, y1, x2, y2);
            line(y2, x2, y1, x1); //square effect
            xoff += inc;
            pop();

        }
        yoff += inc;
        zoff += 0.001;
    }
    // noLoop();
}

