    for (let y = 0; y < PARAMS.maxh; y++){
    ellipse(x * 20, y * 20, PARAMS.radius/8);
    }
  }

  push();
  for (let x = 0; x < PARAMS.maxw; x++) {
    for (let y = 0; y < PARAMS.maxh; y++){

      for( let i = 0; i < PARAMS.lineCount; i++ ){
        const p = i / PARAMS.lineCount;
        const v1 = noise( p, PARAMS.noiseShift );
        const x1 = sin( v1 * TWO_PI ) * PARAMS.radius/15;
        const y1 = cos( v1 * TWO_PI ) * PARAMS.radius/15;

        const v2 = noise( PARAMS.noiseShift, p );
        const x2 = sin( v2 * TWO_PI ) * PARAMS.radius/15;
        const y2 = cos( v2 * TWO_PI ) * PARAMS.radius/15;

        line(x * 20 + x1, y * 20 + y1, x * 20 + x2, y * 20 + y2);

      }
    }
  }
  pop();

}

