const _col = 32;
const _dis = 20;

const _spd = 1;
var _particles = [];

function setup() {
  createCanvas(640, 480);
  angleMode(DEGREES);
  noFill();
  strokeWeight(2);

  // create the particles
  for(let col = 0; col < _col; col++) {
    let param = noise((100 + col * _dis) * 0.01);
    _particles.push(new particle(
      random(50, width - 50), height, 
      map(param, 0, 1, 210, 330), pow(param, 3),
      color(param * 128, param * 220, param * 255)
    ));
  }
}

function draw() {
  for(let pt in _particles) {
    if(_particles[pt].update()) {
      _particles.splice(pt, 1);
    } else {
      _particles[pt].draw();
    }
  }
}

class particle {
  constructor(x, y, dir, frq, color) {
    this.pos_x = x;
    this.pos_y = y;
    this.dir = dir;
    this.frq = frq;
    this.color = color;
    this.head = random() > 0.5 ? true : false;
  }

  update() {
    let d = this.dir;
    if(this.head) this.dir += pow(millis()/1000, 2) * this.frq;
    else this.dir -= pow(millis()/1000, 2) * this.frq

    this.pos_x += cos(this.dir) * _spd;
    this.pos_y += sin(this.dir) * _spd;

    // check if the particle leave the canvas
    // let x = this.pos_x, y = this.pos_y;
    // if(x < 0 || x > width || y < 0 || y > height)
    //   return true;
    // else return false;
    // return false;
  }

  draw() {
    push();
    stroke(this.color);
    point(this.pos_x, this.pos_y);
    pop();
  }
}

