  background(50);
  ellipseMode(CENTER);

  let amplitude = amplitude_slider.value() / 100;

  fill(255);
  noStroke();

  let frequency = frequency_slider.value();

  let timeSpeed = (timeSpeed_slider.value()) / 50;

  noiseDetail(timeSpeed_slider, 0.5);

  // study this loop. do you understand how the line of ellipses is created?
  for (i = 0; i < 1; i += 0.02) {
    let x = lerp(startX, endX, i);
    let y = lerp(startY, endY, i);

    // hint: drive offsetX and offsetY with noise() instead of random()

    let offsetX = (noise(frameCount * timeSpeed + i * frequency, timeSpeed) + 0.5) * amplitude * 20;
    let offsetY = (noise(frameCount * timeSpeed + i * frequency, timeSpeed) + 0.2) * amplitude * 45;
    ellipse(x + offsetX, y + offsetY, 10, 10);
  }
}

