function unit() {
  push();
  noStroke();
  rotate(random([0, 90, 180, 270]));
  fill(color2);
  beginShape();
  vertex(-unit_w/2, unit_w/2);
  vertex(unit_w/2, unit_w/2);
  vertex(unit_w/2, -unit_w/2);
  vertex(-unit_w/2, unit_w/2);
  endShape();
  pop();
}

