unit_w = 10;
unit_h = 13;

trim_w = 2;
//trim_h = 2;

window_w = 4;
window_h = 8;

// Draw/build:
    castleUnit(0, 0, 0);


module castleUnit (x, y, z) {
    // sets origin to base center of shape
    translate([x, y, z + unit_h/2]){
        // base block with window
        difference() {
            // base block
            color("blue"){
                cube([unit_w, unit_w, unit_h], center = true);
            } 
            unitWindow();
        }
        // add trim to base block
        difference() {
            unitTrim();
            cube([unit_w, unit_w, unit_h+1], center = true);
        }
    }
}

module unitTrim () {
    // 4 corners
    translate([-unit_w/2, -unit_w/2, 0]){
        cube([trim_w, trim_w, unit_h], center = true);
    }
    translate([unit_w/2, -unit_w/2, 0]){
        cube([trim_w, trim_w, unit_h], center = true);
    }
    translate([-unit_w/2, unit_w/2, 0]){
        cube([trim_w, trim_w, unit_h], center = true);
    }
    translate([unit_w/2, unit_w/2, 0]){
        cube([trim_w, trim_w, unit_h], center = true);
    }

    // top plate
    translate([0, 0, unit_h/2]){
        cube([unit_w + trim_w, unit_w + trim_w, trim_w], center = true);
    }
}

module unitWindow () {
    translate([0, -unit_w/2, 1]){
        union() {
            translate([0, 0, -window_h/4]) {
                cube([window_w, window_w, window_h - 2], center = true);
            }
            translate([0, 0, window_h/8]) {
                rotate([90, 0, 0]) {
                    cylinder(h = window_w, d = window_w, center = true, $fs = 0.01);
                }
            }
        }
    }

}

