  beginShape();
  vertex(84, -28);
  vertex(233, -36);
  vertex(337, 22);
  vertex(337, 284);
  vertex(264, 293);
  vertex(251, 426);
  vertex(84, 419);
  vertex(-32, 189);
  vertex(-17, 59);
  endShape(CLOSE);
  pop();
}

function nose(a, b) {
  push();
  translate(a, b);
  beginShape();
  vertex(165, 57);
  vertex(218, 24);
  vertex(150, 255);
  vertex(227, 230);
  endShape(CLOSE);
  pop();
}

function hat(a, b) {
  push();
  translate(a, b);
  beginShape();
  vertex(18, -203);
  vertex(158, -5);
  vertex(-45, 140);
  endShape(CLOSE);
