// require https://cdn.jsdelivr.net/npm/p5@latest/lib/p5.min.js

let map = null;
let noiseScale = 1/150;
let colorArr = {
  ocean: '#FBC02D',
  shore: '#FFEE58',
  sand: '#FDD835',
  grass: '#FFEB3B',
  stone: '#FDD835',
  snow: '#FBC02D',
  deepOcean: '#F57F17'
}

let treasurePositionX;
let treasurePositionY;

function setup() {
  createCanvas(windowWidth, windowHeight); 
  noStroke();
  noiseDetail(3, 0.4);
  makeMap();
  drawMap();

  treasurePositionX = random(width);
  treasurePositionY = random(height);
}

function draw() {
 // background(220);
  textFont('Georgia');
  textSize(18);
  text('X', treasurePositionX, treasurePositionY);

  let distanceY = dist(treasurePositionX, treasurePositionY, mouseX, mouseY);
}

function makeMap() {
  map = [];
  for (let x = 0; x < width; x++) {
    map[x] = [];
    for (let y = 0; y < width; y++) {
      map[x][y] = colorMap(x,y);
    }
  }
}

function colorMap(x, y) {
  let colorFactor = noise(x * noiseScale, y * noiseScale);
  let col;

  if (colorFactor < 0.1) {
    col = colorArr.snow;
  }
  else if (colorFactor < 0.2) {
    col = colorArr.ocean;
  }
  else if (colorFactor < 0.3) {
    col = colorArr.shore;
  }
  else if (colorFactor < 0.4) {
    col = colorArr.sand;
  }
  else if (colorFactor < 0.5) {
    col = colorArr.grass;
  }
  else if (colorFactor < 0.55) {
    col = colorArr.stone;
  }
  else if (colorFactor < 0.75) {
    col = colorArr.deepOcean;
  }
  else {
    col = colorArr.stone;
  }
  return color(col);
}

function drawMap() {
  for (let x = 0; x < width; x++) {
    for (let y = 0; y < height; y++) {
      set(x, y, map[x][y]);
      let colorFactor = noise(x * noiseScale, y * noiseScale);
    }
  }
  updatePixels();
}

// require https://cdn.jsdelivr.net/npm/p5@latest/lib/p5.min.js
// I referred to https://p5js.org/reference/#/p5/noise to create this

// hue can be changed with a click, this variable is the beginning hue
  let nextHue = 150;

// noise-based x/y position (needs an initial offset and a variable)
  let moveX;
  let xOff = 0.0;

  let moveY;
  let yOff = 0.0;

function setup() {
  createCanvas(500, 500);
  colorMode(HSB);              // Much easier to step through colors
  background(0);
}

function draw() {
  // I'm translating here because it keeps the noise a bit more centered
    translate(10, 10);

  // setting color
    stroke(nextHue, 100, 100);
    fill(nextHue, 100, 100);

  // x value changes
    xOff += 0.3;
    moveX = noise(xOff) * width;

  // y value changes
    yOff += 0.1;
    moveY = noise(yOff) * height;

  // console.log(`X Noise:${moveX} | Y Noise:${moveY}`);

  // draws line from center to noise location
    line(width/2 -10, height/2 -10, moveX, moveY);

  // the circle in the center grows with your mouseX
    fill(0, 0, 0)
    circle(width/2 -10, height/2 -10, mouseX/10);

  // Comment this section in to make the earlier-drawn ones fade into the background
    // push();
    //   fill(280, 100, 30, 0.01);
    //   noStroke();
    //   rect(-10, -10, width + 10, height + 10);
    // pop();
}

// when the mouse is pressed, the hue increases by 20Â°, and if it gets to 360Â°, it resets
  function mousePressed(){  
    nextHue+=20;

    if(nextHue >= 360){
      nextHue = 0;
