let grow_speed = 0.24;
let initial_size = 0.1;
let num = 80;
let content;
let msg;

function setup() {
  createCanvas(800, 800);
}

function draw() {
  background(params.color);
  noStroke();
  // textFont("Helvetica");
  textFont("Futura");
  textAlign(CENTER);

  msg = params.message;

  let x;
  let y;
  let x_end;
  let y_end;
  let alpha;
  let spacing = (800-80*2)/(msg.length-1);

  for (m = 0; m < msg.length; m++) {
    for (j = 0; j < msg.length; j++) {
      // final position
      x_end = j * spacing + 80;
      y_end = m * spacing + 90;
      noiseDetail(1);
      let n = noise(x_end * 0.02, y_end * 0.02, frameCount*0.1)*20;
      x_end = x_end + n;

      // content
      content = str(msg[j]);

      for (i = 0; i <= num; i++) {

        fill(255, 255, 255, alpha);
        textSize(initial_size + grow_speed * i);
        alpha = lerp(0, 50, 1 / num * i);

        // position
        x = lerp(width / 2, x_end, 1 / num * i);
        y = lerp(height / 2, y_end, 1 / num * i);

        if (i == num) {
          fill(255);
        } else {
          fill(255, 255, 255, alpha);
        }

        text(content, x, y);
      }
    }
  }
}

