function firework() {
  noStroke();
  ellipseMode(CENTER);
  let noiseFrequency = 200;
  for (let i = 0; i < 100; i++) {
    // these points are not scattered in the same way
    // how can you make the arrangement match the challenge?
    let x = map(noise(i * noiseFrequency, frameCount) ,0,1,0,width);
    let y = map(noise(i * noiseFrequency, frameCount / 1000) ,0,1,0, height);

    // the diameter shouldn't always be 10, it needs to vary
    let d = dist(width / 2, height / 2, x, y);
    let diameter = random(10, map(d / 7, 10, 20, 10, 16));

    // the colors also need to change
    // what colorMode would be easiest to work with?
    c1 = color(random(0, 255), random(200, 255), 0, 255 / (frameCount % 100));
    c2 = color(0, random(0, 255), random(200, 255), 255 / (frameCount % 100));
    c3 = color(random(0, 255), 0, random(200, 255), 255 / (frameCount % 100));

    if (d < 50) {
      fill(random([c1]));
    } else if (d < 100) {
      fill(random([c1, c2]));
    } else {
      fill(random([c1, c2, c3]));
    }
    ellipse(x, y + (frameCount % 100) * 6, diameter, diameter);
  }

  // noLoop();
}

