function drawShapes(){
  img = createImage(500,500);
  img.loadPixels();

  for (let x = 0; x<img.width; x++){
    for (let y = 0;y<img.height; y++){
      let cValue = tan(0.25+noise(x/y*20)*frameCount)+cos(2.75+frameCount+noise(x/1000))+sin(0.5+noise(x/2000*frameCount)-noise(y/1200*x))*100;
      let c = [cValue,cValue,cValue,255];

      setQuick(img, x,y,c);
    }
  }
  img.updatePixels();
  noSmooth();
  image(img, 0,0, width,height);
}


function setQuick(img, x, y, c) {
	const i = (y * img.width + x) * 4;

	img.pixels[i + 0] = c[0];
	img.pixels[i + 1] = c[1];
	img.pixels[i + 2] = c[2];
	img.pixels[i + 3] = c[3];
