let myTurtle;
let elephantImg;

function preload(){
  elephantImg = loadImage('elephant.jpg');
}

function setup() {
  createCanvas(1000, 645);

  stroke(0);
  // noStroke();
  pixelDensity(1);

  noLoop();
  myTurtle = new Turtle();
}

function draw() {
  background(120);
  // drawTree(width/2, height/2, -90);

  // const treeCount = 3;
  // for(let copy = 0; copy < treeCount; copy++){
  //   drawTree(width/2, height/2, (360/treeCount)*copy);
  // }
  // image(elephantImg, 0, 0, width, height);
  elephantImg.loadPixels();


  // Loop structure and performance check from https://compform.net/pixels/
  const start = performance.now();

  // loop over every x,y pixel coordinate in the image
  for (let x = 1; x < width; x += 10) {
    for (let y = 1; y < height; y += 10) {
      const pixelRed = getQuick(elephantImg, x, y)[0];
      const pixelGreen = getQuick(elephantImg, x, y)[1];
      const pixelBlue = getQuick(elephantImg, x, y)[2];

      const val = (pixelRed + pixelGreen + pixelBlue)/3;

      // Drawing trees based on pixel values
      if (val <= 40){
        stroke(0);
        const treeCount = 5;
        for(let copy = 0; copy < treeCount; copy++){
          drawTree(x, y, (360/treeCount)*copy);
        }
      } else if ( val > 40 && val <= 80){
        stroke(val);
        const treeCount = 2;
        for(let copy = 0; copy < treeCount; copy++){
          drawTree(x, y, -90);
        }
      } else if (val > 80 && val <=100){
       stroke(val);
        const treeCount = 3;
        for(let copy = 0; copy < treeCount; copy++){
          drawTree(x, y, (360/treeCount)*copy);
        }
      } else if (val > 240){
        stroke(200);
        const treeCount = 4;
        for(let copy = 0; copy < treeCount; copy++){
          drawTree(x, y, (360/treeCount)*copy - 90);
        }
      }
    }
  }

  const end = performance.now();

  console.log(`took ${floor(end - start)} ms`);

  noLoop();
}

// getQuick from https://compform.net/pixels/
// find the RGBA values of the pixel at x, y in the img.pixels array
function getQuick(img, x, y) {
  const i = (y * img.width + x) * 4;
  return [
    img.pixels[i],
    img.pixels[i + 1],
    img.pixels[i + 2],
    img.pixels[i + 3],
  ];
}


function drawTree(x, y, angle){
  myTurtle.penUp();
  myTurtle.moveTo(x, y);
  myTurtle.turnTo(angle);
  myTurtle.penDown();
  const branchLength = 20;
  drawBranch(branchLength);
}

// drawBranch modified from Justin Bakse's "Recursive Tree I" sketch on https://compform.net/turtles/
function drawBranch(length) {
  if (length < 10) {
    return;
  }

  // draw this branch
  myTurtle.moveForward(length + random(-5, 5));

  // left child
  myTurtle.pushState();
  myTurtle.turnLeft(20 + random(-10, 10));
  // The randomization herre makes it more scraggly
  drawBranch(length * 0.86);
  myTurtle.popState();

  // right child
  myTurtle.pushState();
  myTurtle.turnRight(20 + random(-10, 10));
  // The randomization herre makes it more scraggly
  drawBranch(length * random(0.75, 0.85));
  myTurtle.popState();
}

