function spinnyText(){
  push();
  translate(-width / 2, -height / 2, 0);
  for(let x = 0; x < width; x += PIXELSIZE){
    for(let y = 0; y < height; y += PIXELSIZE){
      let i = (x + y * width) * 4;
      if(i < 10){
        cube(x, y, PIXELSIZE, 'yellow', false);
      }else if(pixels[i] < 200 && pixels[i + 1] < 200){
        cube(x, y, PIXELSIZE, 'blue', true);
      }else{
        cube(x, y, PIXELSIZE, 'blue', false);
      }
    }
  }
  pop();
}

function dummyText(){
  push();
  translate(-width / 4, 0, 0);
  for(let i = 0; i < points.length; i++){
    const p = points[i];
    cube(p.x, p.y, 10, 'red');
  } 
  pop();
}

function fillCanvas(){
  push();
  translate(-width / 2, -height / 2, 0);
  let rows = floor(width / PIXELSIZE);
  let cols = floor(height / PIXELSIZE);

  for(let i = 0; i < cols; i++){
    for(let j = 0; j < rows; j++){
      cube(i * PIXELSIZE + 1, j * PIXELSIZE + 10, PIXELSIZE, 'red'); 
    }
  }
  pop();
}

function cube(x, y, size, color, invert) {
  push();
  translate(x, y);

  // if()

  let n = floor(noise(x * 0.1) * 10);

  // console.log(n);

  let r = map(frameCount, 0, n, 0, PI);

  rotateY(frameCount * 0.2);
  stroke('black');
  fill(color);
  box(size, size, size);
  if(invert){
    fill('white');
    stroke('white');
    translate(0, 0, size / 2 + 1);
    plane(size, size);
  }
  pop(); 
}

