// inspired by https://openprocessing.org/sketch/1233942
// CompForm Co-coding challenge #1
// Sonya and Olivier

let layers = 5;
let centerCircleObj;
let t = 0;
let randomColorArr = [];
let randomRBg;
let randomGBg;
let randomBBg;

function setup() {
  createCanvas(windowWidth, windowHeight);
  let randomStart = 0.3*width;
  let randomEnd = 0.45*width;
  let randomCircleWidth = (random(randomStart, randomEnd) + random(randomStart, randomEnd) + random(randomStart, randomEnd) + random(randomStart, randomEnd))/4;
  centerCircleObj = new centerCircle(randomCircleWidth);


  for (let i = 0; i < 7; i++) {
    randomColorArr[i] = color(floor(random(150,255)) + 1, floor(random(150,255)) + 1, floor(random(150,255)) + 1);
  }

  randomRBg = floor(random(0,80));
  randomGBg = floor(random(0,80));
  randomBBg = floor(random(0,80));
}

function draw() {
  background(randomRBg,randomGBg,randomBBg);
  centerCircleObj.draw();
  centerCircleObj.drawInnerCircle();
}

class centerCircle {
  constructor(w) {
    this.x = 0;
    this.y = 0;
    this.t = 0;
    this.w = ceil(w);
    this.startR = 0;
    let randomValue = floor(random(2,6) + random(2,6) + random(2,6)/3);
    this.innerCircles = randomValue;
    this.innerCircleMax = floor(w - ceil(w/randomValue) - 10);
    this.circleDifferentiater = floor((floor(w - ceil(w/randomValue)) - 20)/randomValue);
    this.randomR = random(150,250);
    this.randomG = random(0,50);
    this.randomB = random(75,120);

    this.innerStartOp = 0;
  }

  draw() {
    push();
      blendMode(LIGHTEST);
      translate(width/2, height/2);
      push();
        fill(randomColorArr[0]);
        circle(this.x - (this.startR/2), this.y, this.startR - 0.175*this.startR);
      pop();
        push();
          fill(randomColorArr[1]);
          circle(this.x, this.y, this.startR);
      pop();
      push();
          noFill();
          stroke(randomColorArr[5]);
          strokeWeight(10);
          circle(this.x, this.y, this.startR*1.3);
          stroke(randomColorArr[6]);
          circle(this.x, this.y, this.startR*1.5);
        pop();
      push();
       fill(randomColorArr[2]);
       circle(this.x + (this.startR/2), this.y, this.startR - 0.175*this.startR);
      pop();
      push();
       fill(randomColorArr[3]);
       circle(this.x, this.y + (this.startR/2), this.startR - 0.175*this.startR);
      pop();
      push();
       fill(randomColorArr[4]);
       circle(this.x, this.y - (this.startR/2), this.startR - 0.175*this.startR);
      pop();
    pop();
    this.startR = lerp(this.startR, this.w, 0.04);
  }

  drawInnerCircle() {
    push();
      translate(width/2, height/2);
    let angle = Math.PI*2 / 20;
      if (this.startR > 0.95*this.w) {
        for(let j = 0; j < this.innerCircles; j++){
          let maxRadius = this.innerCircleMax - (j * this.circleDifferentiater);
          for (let i = 0; i < 20; i++) {
            noStroke();
            let x = cos(angle * i) * (maxRadius * 0.5);
            let y = sin(angle * i) * (maxRadius * 0.5);
            fill(floor(noise(t) * this.randomR), floor(noise(t) * this.randomG), floor(noise(t) * this.randomB), this.innerStartOp);
            circle(x,y, 5);
          }
        }
        t = t+0.01;

        this.innerStartOp = lerp(this.innerStartOp, 255, 0.01);
      pop();    
    }
  }
}

