    createCanvas(400, 400);
  }

  function draw() {
    background(50);

    noStroke();
    ellipseMode(CENTER);

    let noiseFrequency = 10;

    for (let i = 0; i < 100; i++) {
      //use noise value instead of random
      //i had to reduce width and height to keep circles in the middle 
      let x = noise(i*noiseFrequency,0)*(width);
      let y = noise(i*noiseFrequency,1000)*(height);

      let n = noise(i*noiseFrequency, 15);

      // using noise and map to change circle diameter
      let diameter = map(n,0,1,0,20);

      colorMode(HSB);
      let c = map(diameter,3,20,80,360);
      fill(c,c,c);

      ellipse(x, y, diameter, diameter);
    }

    noLoop();
  }

