function clump() {
  noStroke();
    for (let xoff=0; xoff < 1500; xoff++){
      fill(0,(xoff/15)+100,0);
  let dotloc = map(noise(xoff)+random(-.01,1),0,1,0,400)
  let dotloc2 = map(noise(xoff)+random(-.01,1),0,1,0,400)
  let dotsize = map(noise(xoff),0,1,1,10)
  ellipse(dotloc2,dotloc,dotsize,dotsize);
  }
}

