// require https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js

function setup() {
  createCanvas(400, 400);
  colorMode(HSL);
  background(20);
}

function draw() {
  noStroke();
  ellipseMode(CENTER);

  for (let i = 0; i < 30; i++) {
    // var points = [];
    let x = noise(i * 4) * width;
    let y = (noise(i * 40) * height) / 2;
    // push.points(points[i].x, points[i].y);
    let diameter = (noise(i * 0.1) * 180) / Math.abs(200 - x) + 10;
    let colorRange =
      (noise(i * 0.1) * 330 * 80) / (Math.abs(200 - x) + Math.abs(200 - y));

    let offsetY = y * 0.01 + frameCount * i;
    fill(colorRange, 100, 60);
    ellipse(x, y + offsetY, diameter, diameter);
  }
}

