function setup() {
	createCanvas(800, 800);
	background(255)
	noStroke();
}

function draw() {
	if (go) {
		if (int(frameCount % (30 / params.speed)) == 0) {
			let sizer = (random(3, params.size*4) + random(3, params.size*4)) / 2
			drawTwirlyLines(params.shape, params.spacing, params.noise, sizer);
		}
	}
}

function drawShape(shape, x, y, size) {
	switch (shape) {
		case 0:
			circle(x, y, size);
			break;
		case 1:
			rect(x, y, size, size);
			break;
		case 2:
			triangle(x, y, x + size, y, x + size / 2, y + size);
			break;
		case 3:
			ellipse(x, y, size, size * 2);
			break;
	}
}


function drawTwirlyLines(shape, spacing, noiseSpacing, sizer) {
	let dir = random(['left', 'right', 'up', 'down']);

	// move to the right
	if (dir == 'right') {
		// pick a distance
		xDist = floor(random(0, width / 3));
		// make sure we're not exceeding the page
		while (xCur + xDist > width) {
			xDist--;
		}
		// move in that direction
		for (let i = 0; i < xDist; i += spacing) {
			let n = noise(i) * noiseSpacing;
			fill('#000000')
			drawShape(shape, i + xCur, yCur + n, sizer);
		}
		// increment
		xCur = xCur + xDist;
		// move down
	} else if (dir == 'down') {
		yDist = floor(random(0, height / 3));
		while (yCur + yDist > height) {
			yDist--;
		}
		for (let i = 0; i < yDist; i += spacing) {
			let n = noise(i) * noiseSpacing;
			fill('#BE1E2D')
			drawShape(shape, xCur + n, i + yCur, sizer);
		}
		yCur = yCur + yDist;
		// move left
	} else if (dir == 'left') {
		xDist = floor(random(0, width / 3));
		while (xCur - xDist < 0) {
			xDist--;
		}
		for (let i = 0; i < xDist; i += spacing) {
			let n = noise(i) * noiseSpacing;
			fill('#FFDE17')
			drawShape(shape, xCur - i, yCur - n, sizer);
		}
		xCur = xCur - xDist;
		// move up
	} else if (dir == 'up') {
		yDist = floor(random(0, height / 3));
		while (yCur - yDist < 0) {
			yDist--;
		}
		for (let i = 0; i < yDist; i += spacing) {
			let n = noise(i) * noiseSpacing;
			fill('#21409A')
			drawShape(shape, xCur - n, yCur - i, sizer);
		}
		yCur = yCur - yDist;
	}
}

