const INCH = 72;
const N_MASKS = 30;
const ROWS = 6;
const COLS = 5;
const GAP = 2;

let saveButton;
let masks = [];

// tweakpane params
const params = {
    index: 0,
    lineweight: 0.5,
    radius: INCH / 4,
    points: 30,
    wiggle: 5,
    randomcolors: false,
    randomwiggle: false
}
const pane = new Tweakpane.Pane();

function setup() {
  createCanvas(windowWidth, windowHeight, SVG);
  tewakpaneSetup();
  rectMode(CENTER);
  textAlign(LEFT);

  // button for saving the svg
  saveButton = createButton('save svg');
  saveButton.position(width - 100, height - 50);
  saveButton.mousePressed(save);

  let offset = GAP + INCH;
  // create masks
  for(let i = 0; i < N_MASKS; i++){
    masks[i] = new Mask(i, INCH / 4 + i, 30, 5, {x: offset + 2 * (offset) * (i % ROWS), y: offset + 2 * (offset) * floor(i / ROWS)});
  }

  // let's not
  noLoop();
};

function draw() {
  background(250);
  strokeWeight(params.lineweight);

  // iterate over the masks
  for(let i = 0; i < masks.length; i++){
    // if one is selected, then update the parameters
    if(floor(params.index) == i){
      masks[i].setParams();
    }
    // draw
    masks[i].draw();
  }
};

function Mask(index, radius, points, wiggle, center){
  this.index = index;
  this.radius = radius;
  this.points = points;
  this.wiggle = wiggle;
  this.center = center;
  this.frame = true;
}

Mask.prototype.setParams = function(){
  // floor to avoid bad math
  this.radius = floor(params.radius);
  this.wiggle = floor(params.wiggle);
  this.points = floor(params.points);
}


Mask.prototype.drawFrame = function(){
  stroke(255, 0, 0);
  // outer frame
  rect(0, 0, 2 * INCH, 2 * INCH, 10, 10, 10, 10);
  // inner frame
  // line(-INCH + 20, -INCH + 20, INCH / 4 - 20, -INCH + 20);
  // line(INCH / 4 - 20, -INCH + 20, INCH / 4 - 20, -INCH + INCH - this.radius);
  rect(0, 0, 2 * INCH - 30, 2 * INCH - 30, 10, 10, 10, 10);
}


Mask.prototype.draw = function(){
  push();

  translate(this.center.x, this.center.y);

  if(this.frame == true){
    // draw the slide frame
    this.drawFrame();
    // add index number in the corner
    stroke(0, 0, 255);
    text(this.index, INCH - 1.5 * textWidth(this.index), INCH - 5);
  }

  // initial point
  let x1 = this.radius * cos(0);
  let y1 = this.radius * sin(0);

  for(let i = 1; i < this.points; i++){
    let x2 = this.radius * cos(i * 2 * PI / this.points);
    let y2 = this.radius * sin(i * 2 * PI / this.points);

    let xA, yA, xB, yB;

    // imagine 0, 0 at center
    // first quadrant
    if(x2 > 0 && y2 < 0){
      xA = x1 + -this.wiggle;
      yA = y1 + this.wiggle;
      xB = x2 + -this.wiggle;
      yB = y2 + this.wiggle;
    // second quadrant
    }else if(x2 < 0 && y2 < 0){
      xA = x1 + this.wiggle;
      yA = y1 + this.wiggle;
      xB = x2 + this.wiggle;
      yB = y2 + this.wiggle;
    // third quadrant
    }else if(x2 < 0 && y2 > 0){
      xA = x1 + this.wiggle;
      yA = y1 + -this.wiggle;
      xB = x2 + this.wiggle;
      yB = y2 + -this.wiggle;
    // fourth quadrant
    }else{
      xA = x1 + -this.wiggle;
      yA = y1 + -this.wiggle;
      xB = x2 + -this.wiggle;
      yB = y2 + -this.wiggle;
    }

    stroke(255, 0, 0);

    if(i == this.points - 1){
      curve(xA, yA, x1, y1, this.radius * cos(0), this.radius * sin(0), xB, yB);
    } else {
      curve(xA, yA, x1, y1, x2, y2, xB, yB);
    }

    x1 = x2;
    y1 = y2;
  }

  pop();
}

