function lines_1() {
  for (let x = 0; x <= width; x += lineAgain) {
    let x1 = x;
    let y1 = 200;
    let x2 = x;
    let y2 = 400;

    // we're changing x1 value with noise
    // noise returns values 0 to 1
    // we're subtracting 0.5 to include negative numbers
    // then * by a 10x number to make noticeable variation
    x2 += (noise(x) - 0.5) * nVary;
    // console.log((noise(x)) * x);  // all between 0 and 1

    line(x1, y1, x2, y2);
  }
}

function lines_2() {
  for (let x = 0; x <= width; x += lineAgain) {
    let x1 = x;
    let y1 = 200;
    let x2 = x;
    let y2 = 400;

    x1 += (noise(x) - 0.5) * nVary;
    x2 += (noise(x + 5) - 0.5) * nVary;

    line(x1, y1, x2, y2);
  }
}

function lines_3() {
  for (let x = 0; x <= width; x += lineAgain) {
    let x1 = x;
    let y1 = 200;
    let x2 = x;
    let y2 = 400;

    x1 += (noise(x + 5) - 0.5) * nVary;

    line(x1, y1, x2, y2);
  }
}

