var cursorPos;

var scroll = 0;
const defSize = 12;
const dens = 37;

function setup() {
  createCanvas(640, 480);
  noStroke();

  // Init the cursor pos
  cursorPos = createVector(0, 0);
}

function draw() {
  background(0);

  updateCursor();
  drawPixels();
}

function updateCursor() {
  cursorPos.x = mouseX;
  cursorPos.y = mouseY;
}

function drawPixels() {
  // update scroll position
  if(scroll < dens - defSize / 2) scroll += 0.2;
  else scroll = 0 - defSize / 2;

  // update pixels
  for(let i = 0; i < width / dens + 1; i++) {
    for(let j = 0; j < height / dens; j++) {
      // scroll
      let x = scroll + i * dens, y = j * dens + dens/2;

      // calculate radius
      let dis = dist(x, y, cursorPos.x, cursorPos.y);
      let size = defSize + 1200 / (dis + 30);

      // draw the pixel
      fill(110 * size / defSize)
      ellipse(x, y, size);
    }
  }
}

