class Dot {
	constructor (radius, brightness, alpha) {
		const r = random(TWO_PI)
		const x = (width / 2 + sin(r) * radius)
		const y = height / 2 + cos(r) * radius
		this.pos = createVector(x,y)
		this.prev = createVector(x,y)
		this.color = color(255)
		this.deadCount = 0
		this.radius = radius
		this.alpha = alpha
	}

	update(noize) {
		this.v = p5.Vector.fromAngle(noize * TWO_PI + (this.deadCount * PI))
		this.v.setMag(2)
		this.color = color(random(255,120), 100, random(100,240), this.alpha)
		this.prev = this.pos.copy()
		this.pos = this.pos.add(this.v)

		if (dist(width/2, height/2, this.pos.x, this.pos.y) > this.radius + 2) {
			this.deadCount+=3;
		}
	}

	draw() {
