let song, analyzer;

function preload() {
  song = loadSound("assets/bg.mp3");
}

let w;
let inc = 0.01;

function setup() {
  createCanvas(windowWidth, windowHeight);
  noiseDetail(20);
  song.loop();
  // create a new Amplitude analyzer
  analyzer = new p5.Amplitude();
  // Patch the input to an volume analyzer
  analyzer.setInput(song);
}

function draw() {
  w = windowWidth / 16;
  background(0);
  // Get the average (root mean square) amplitude
  let rms = analyzer.getLevel();
  fill(127);
  stroke(0);
  // apply noise
  var xoff = 0;
  for (var x = w / 2; x < width; x += w) {
    var yoff = 0;
    for (var y = w / 2; y < height; y += w) {
      noFill();
      c = map(rms, 0, 0.2, -20, 255);
      stroke(255, c);
      strokeWeight(w / 12);
      strokeCap(ROUND);
      push();
      translate(x, y);
      m = map(rms, 0, 0.2, 1, 2);
      n = map(noise(xoff, yoff, frameCount * 0.001 * m), 0, 1, 0, 40);
      rotate(n);
      line(-w / 3, 0, w / 3, 0);
      pop();
      yoff += inc;
    }
    xoff += inc;
  }
}

