function setup() {
  createCanvas(600, 600);
  noStroke();
  angleMode(DEGREES);

  // set up the panel
  panelParam.Size = 5;
  panelParam.Density = 50;
  panelParam.Speed = 5;
}

function draw() {
  background(colorFormat(panelParam.bgColor));

  let groundColor = panelParam.solidBgColor ? colorFormat(panelParam.bgColor) : colorFormat(panelParam.btColor);

  gradientBackground(0, height - height * 0.3, width, height * 0.3, colorFormat(panelParam.bgColor), groundColor);

  if(frameCount % (11 - round(panelParam.Density / 10)) === 0) {
  // randomly generate snowflakes
    if(random(100) < map(panelParam.Density, 10, 100, 10, 80)) {
      createSnow(
        random(width), -100, 
        random(minSize, maxSize), 
        random(minSpd, maxSpd));
    }
  }

  // update snowflakes
  if(snowFlakes.length > 0) {
    for(let f in snowFlakes) {
      if(snowFlakes[f].update()) {
        snowFlakes.splice(f, 1);
      } else {
        snowFlakes[f].draw();
      }
    }
  }
}

class SnowFlake {
  constructor(x, y, size, spd) {
    this.x = x;
    this.ox = x;
    this.y = y;
    this.size = size;
    this.layer = round(abs(size - maxSize) / 3) + 10;

    this.spd = spd;
    this.ospd = spd;
    this.swing = map(spd, minSpd, maxSpd, 0.4, 1.2);
    // this.rotateSpd
    this.alp = map(this.spd, minSpd, maxSpd, 20, 80);
  }

  update() {
    // reset the speed
    this.spd = this.ospd + map(panelParam.Speed, 1, 10, -0.4, 0.8);
    this.swing = map(this.spd, minSpd, maxSpd, 0.4, 1.2);

    this.y += this.spd;
    this.x += map(noise(this.x / 100, this.y / 100), 0, 1, 0-this.swing, this.swing);

    // check if the flake leave the canvas
    let x = this.x, y = this.y, s = this.size;
    if(x < 0 - s || x > width + s || y > height + s)
      return true;
    else return false;
  }

  draw() {
    let x = this.x, y = this.y, s = this.size;
    let col = color(panelParam.Color.r, panelParam.Color.g, panelParam.Color.b);
    push();
    col.setAlpha(this.alp);
    fill(col);
    for(let l = 0; l < this.layer; l++) {
      circle(x, y, (s - l * 3) * map(panelParam.Size, 1, 10, 0.3, 2));
    }
    pop();
  }
}
// create a new snowflake
function createSnow(x, y, size, spd) {
  snowFlakes.push(new SnowFlake(x, y, size, spd));
}

function gradientBackground(x, y, w, h, col1, col2) {
  push();
  for(let i = 0; i < h; i += 0.1) {
    let cur_color = lerpColor(col1, col2, map(i, 0, h, 0, 1));
    stroke(cur_color);
    line(x, y + i, x + w, y + i);
  }
  pop();
}

function colorFormat(TweakColor) {
  let normalColor = color(TweakColor.r, TweakColor.g, TweakColor.b);
  return normalColor;
}

// require https://cdn.jsdelivr.net/npm/p5@latest/lib/p5.min.js
const STEM_LENGTH = 100;
const STEM_SEGMENT = 0.1;
const RADIUS = 50;
const SPIKE_WIDTH = 5;
const SPIKE_HEIGHT = 10;


function setup() {
  createCanvas(800, 800, WEBGL);
}

function draw() {
  background(250);

  orbitControl();

  normalMaterial();
  sphere(RADIUS);
  stem(0, RADIUS, 0);


  for(let x = 0; x < 2 * PI; x += 2 * PI / 8){
    push();
    let xP = cos(x) * RADIUS * 1.1;
    let yP = sin(x) * RADIUS * 1.1;
    spike(xP, yP, 0, x);
    pop();
  }
}



function spike(x, y, z, a){
  push();

  translate(x, y, z);
  rotate(-PI / 2 + a);

  cone(SPIKE_WIDTH, SPIKE_HEIGHT);

  pop();
}

function stem(x, y, z){
  push();
  translate(x, y, z);
  let segments = STEM_LENGTH * 1 / STEM_SEGMENT;
  for(let i = 0; i < segments; i++){
    let xOffset = map(noise(i * 0.0008), 0, 1, -STEM_SEGMENT, STEM_SEGMENT);
    translate(x + xOffset, STEM_SEGMENT);
    if(i > 9 * segments / 10){
      cylinder(3 + (i - 9 * segments / 10) * 0.04, STEM_SEGMENT);
    } else {
      cylinder(3, STEM_SEGMENT);
    }
  }
  pop();
}

