  t.moveTo(posx, posy);

  t.penDown();
  let turnAng = 180 - 360/npoints;

  for (let i = 0; i < npoints; i++) {

    strokeWeight(sin(i)*0.5);
    t.moveForward(r);
    t.turnRight(iang*turnAng);
  }

}

