function flower(x, y, size, petals, smoothness){
    strokeWeight(80);
    stroke(random(200, 255), random(30), random(100));
    let myTurtle = new Turtle();
    myTurtle.penUp();
    // calculate petal angles
    let petalAngle = 360 / petals;
    let interiorAngle = 90 - petalAngle / 2;
    // move to the location of the center
    myTurtle.moveTo(x, y);
    myTurtle.penDown();
    for(let i = 0; i < petals; i++){
      myTurtle.moveForward(size);
      // turn to be tangential to the petal
      myTurtle.turnLeft(90 - interiorAngle);
      // increment angle for the petal lobe
      let lobeInc = 180 / smoothness;
      // chord across the end of the petal
      let lobeRadius = 2 * size * sin(radians(petalAngle / 2));
      // turn a bit then move forward 
      for(let i = 0; i < smoothness; i++){
        myTurtle.turnLeft(lobeInc / 2);
        myTurtle.moveForward(lobeRadius * sin(radians(lobeInc / 2)));
        myTurtle.turnLeft(lobeInc / 2);
      }
      myTurtle.turnLeft(90 - interiorAngle);
      myTurtle.moveForward(size);
      // back to the direction we came
      myTurtle.turnLeft(180);
    }
    myTurtle.penUp();
}



class Leaf {
  constructor(x, y, a, l) {
    this.startPos = createVector(x, y)
    this.angle = a
    this.length = l
    this.endPos = createVector(0, 0)
    // this.length = this.startPos.copy().sub(this.endPos).mag()
    this.ts = []
    for (let i = 0; i < 3; i++) {
      this.ts.push(new Turtle(x, y))
    }

    this.draw()
  }

  draw() {
    var leafAngle = 60
    var turtleStepLength = 5

    this.ts.forEach(element => {
      element.turnTo(this.angle)
      element.penDown();
    });

    var [tl, tm, tr] = this.ts
    tl.addTail()
    tl.tail.visit(tm)
    tr.addTail()
    tr.tail.visit(tm)
    //turn to initial angle
    // stroke(96, 108, 56)
    push()
    // stroke(40, 54, 24)
    tm.penUp()
    tm.moveForward(this.length)
    pop()
    this.endPos = createVector(tm.x, tm.y)
    tm.moveBackward(this.length)
    tm.penDown()
    tl.turnLeft(leafAngle);
    tr.turnRight(leafAngle);

    //draw outline
    var turtleList = [tr, tl, tm];
    var arrived = false
    var d//temp distance
    var count = 0
    while (!arrived && count < 500) {
      var angleToTarget = 0;
      //moveforward 1 step
      turtleList.forEach(element => {

        angleToTarget = this.endPos.copy().sub(element.x, element.y).heading() //get angle to Target
        var a = random(-10, 10) + element.bearingRadians
        var distance = createVector(tr.x, tr.y).add(tl.x, tl.y).mult(0.5).sub(this.endPos).mag()
        d = distance
        a = map((distance / this.length), 1, 0, a + degrees(element.bearingRadians), degrees(angleToTarget))

        element.turnTo(a)

        element.moveForward(turtleStepLength);


      });

      //check if each turtle arrives
      turtleList.forEach((element, index, array) => {
        try {
          while (element.arrivingTarget(this.endPos, turtleStepLength)) {
            element.moveToV(this.endPos);
            // element.tail.closeTail()

            array.splice(index, 1)
            element = array[index]
          }
        }
        catch (error) {

        }
      });


      //check if all turtle arrives
      if (turtleList.length == 0) {
        arrived = true
      }
      count++
    }

  }

}

// Turtle
// Basic turtle graphics implementation:-----------------------------------------------------------
// Basic turtle graphics implementation:-----------------------------------------------------------
class TurtleTail {
  constructor(t1) {
    this.tail = t1.copy()
    this.visitStack = []
    this.stepStack = []
  }

  visit(t) {
    if (t.id != null) {
      this.visitStack.push(t)
    } else {
      console.log("Need to visit a turtle")
    }
  }

  closeTail(){
    if(this.tail!= null){
      push()


      this.stepStack.forEach(element => {
        this.tail.moveToV(element)
      });
      pop()

    }

  }

  follow(t1) {
    push()

    this.stepStack.push(createVector(t1.x, t1.y))
    if (this.stepStack.length > 5) {
      this.tail.moveToV(this.stepStack.shift())
    }
    var currentPos = this.tail.getPosV()
    this.visitStack.forEach(element => {
      this.tail.moveTo(element.x, element.y)
      this.tail.moveToV(currentPos)
    });

    pop()
  }
}

// Turtle
// Basic turtle graphics implementation:
// https://en.wikipedia.org/wiki/Turtle_graphics
// For more info on Javascript OOP:
// https://developer.mozilla.org/en-US/docs/Web/JavaScript/Introduction_to_Object-Oriented_JavaScript
//
// The turtle's coordinate system uses pixels for distance and degrees for rotations
// 0 degrees is straight right (east); positive degrees are clockwise

// Turtle constructor
// takes optional x, y starting coordinates (default is center of sketch)
function Turtle(x, y, id) {
  // assign default values to x and y if they were not passed
  if (typeof x === "undefined") {
    x = width * 0.5;
  }
  if (typeof y === "undefined") {
    y = height * 0.5;
  }
  this.id = turtleID++
  this.x = x;
  this.y = y;
  this.tail
  this.bearingRadians = 0;
  this.isPenDown = true;
  this._st
  ateStack = [];
}
Turtle.prototype.closeTail = function () {
  this.tail.closeTail()
};

Turtle.prototype.addTail = function () {
  this.tail = new TurtleTail(this)
};
Turtle.prototype.getPosV = function () {
  return createVector(this.x, this.y)
};
Turtle.prototype.arrivingTarget = function (target, stepLength) {
  var d = target.copy().sub(this.x, this.y).mag()

  return d < stepLength
};

Turtle.prototype.copy = function () {

  return new Turtle(this.x, this.y)
};

// moveTo instantly transports the turtle to the provided x, y location, drawing a line if pen is down
Turtle.prototype.moveToV = function (pos) {
  if (this.isPenDown) {

    line(this.x, this.y, pos.x, pos.y);
  }
  this.x = pos.x;
  this.y = pos.y;
};

Turtle.prototype.moveTo = function (newX, newY) {
  if (this.isPenDown) {
    line(this.x, this.y, newX, newY);
  }
  this.x = newX;
  this.y = newY;
};

// moveForward moves the turtle along its current bearing, drawing a line if pen is down
Turtle.prototype.moveForward = function (distance) {
  var newX = this.x + cos(this.bearingRadians) * distance;
  var newY = this.y + sin(this.bearingRadians) * distance;
  this.moveTo(newX, newY);
  if (this.tail != null) {
    this.tail.follow(this)
  }
};

// moveBackward moves the turtle backward from its current bearing, drawing a line if pen is down
Turtle.prototype.moveBackward = function (distance) {
  this.moveForward(-distance);
};

// turnTo changes the turtle's bearing to the provided angle in degrees
Turtle.prototype.turnTo = function (angleDegrees) {
  this.bearingRadians = radians(angleDegrees);
};

// turnRight rotates the turtle's bearing clockwise by the provided angle in degrees
Turtle.prototype.turnRight = function (amountDegrees) {
  this.bearingRadians += radians(amountDegrees);
};

// turnLeft rotates the turtle's bearing counter-clockwise by the provided angle in degrees
Turtle.prototype.turnLeft = function (amountDegrees) {
  this.bearingRadians -= radians(amountDegrees);
};

// penUp tells the turtle to move without drawing
Turtle.prototype.penUp = function () {
  this.isPenDown = false;
};

// penDown tells the turtle to draw a line when it moves
Turtle.prototype.penDown = function () {
  this.isPenDown = true;
};

// pushState records the turtle's current state (position, bearing, etc.) to a stack so that changes can be undone easily
Turtle.prototype.pushState = function () {
  this._stateStack.push({
    x: this.x,
    y: this.y,
    bearingRadians: this.bearingRadians,
    isPenDown: this.isPenDown,
  });
};

// popState restores the turtle's state to the top recorded state on the stack
Turtle.prototype.popState = function () {
  if (this._stateStack.length === 0) {
    console.error(
      "Turtle: No states left on stack. Make sure your calls to .pushState and .popState are balanced."
    );
    return;
  }
  var state = this._stateStack.pop();
  this.x = state.x;
  this.y = state.y;
  this.bearingRadians = state.bearingRadians;
  this.isPenDown = state.isPenDown;
};

// image draws and image centered on the turtle's current location and alligned with the turtle's rotation (forward = up)
Turtle.prototype.image = function (i, w, h) {
  // w, h are optional parameters to this function and to p5's image
  // p5's image function will draw the image at its "normal" size if w and h are undefined

  push();
  translate(this.x, this.y);
  rotate(this.bearingRadians + PI * 0.5);
  imageMode(CENTER);
  image(i, 0, 0, w, h);
  pop();
};

