let myTurtle;

function setup() {
  createCanvas(500, 500);
  noFill();
  stroke(255);
  background(50);
  noLoop();
  myTurtle = new Turtle();
}

function draw() {
  push();
  textSize(20);
  text('Stop Smoking',200,450)
  pop();
  myTurtle.penUp();
  myTurtle.moveTo(250, 10);
  myTurtle.turnTo(90);
  myTurtle.penDown();
  drawTrunk();
}


function drawTrunk() {

  myTurtle.moveForward(100);

  myTurtle.pushState();
  myTurtle.turnLeft(30);
  drawLung(200, 15.3);
  myTurtle.popState();


  myTurtle.pushState();
  myTurtle.turnRight(30);
  drawLung(200, 15);
  myTurtle.popState();

}

function drawLung(length, angle) {
  if (length < 50) {
    myTurtle.moveForward(10);
    return;
  }

  for(let i = 0; i < length; i++ ) {

    const shouldBranch = (floor(random(0, length)) < i/8.5);
    if (shouldBranch) {

      myTurtle.pushState();

      let realAngle = random(10, angle);

      const shouldTurnLeft = (random(0,1) < 0.5);
      if (shouldTurnLeft) {
        stroke(200,random(120,200),40);
        myTurtle.turnLeft(realAngle);
        // myTurtle.turnLeft(random(30,60));
      } else {
        stroke(random(100,230),220,50);
        myTurtle.turnRight(realAngle);
        // myTurtle.turnRight(random(30,60));
      }


      drawLung(length/1.8, Math.min(90, angle * 6));
      myTurtle.popState();

    }

    myTurtle.moveForward(1);

  }

}

