// require https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js

var angle = 2.0;
var scalar = 3.5;
var speed = 0.01;

let spiralImage;

function setup(){
  createCanvas(windowWidth, windowHeight);
  spiralImage = createImage(width, height);
  rectMode(CENTER);
  background(255);
  // background(0);
  noLoop();
}

function draw() { 
  spiralImage.loadPixels();

  for(let i = 0; i < 4; i++){
    let c = [random(255), random(255), random(255)];
    pixelSpiral(random(width / 4, 3 * width / 4), random(height / 4, 3 * height / 4), random(1000, 100000), c); 
  }

  spiralImage.updatePixels();
  image(spiralImage, 0, 0, width, height);
}

function pixelSpiral(xOffset, yOffset, maxIn, c){
  for(let i = 0; i < maxIn; i++){
    let x = floor(xOffset + cos(angle) * scalar);
    let y = floor(yOffset + sin(angle) * scalar);
    angle += speed;
    scalar += speed;
    setQuick(spiralImage, x, y, c);
    setQuick(spiralImage, x + 1, y + 1, c);
    setQuick(spiralImage, x - 1, y - 1, c);
  }
}

function brush(x, y, size){
  let c = [];
  if(whiteBlack){
    c[0] = 0;
    c[1] = 0;
    c[2] = 0;
  }else{
    c[0] = 255;
    c[1] = 255;
    c[2] = 255;
  }
  for(let i = 0; i < 2 * PI; i += 2 * PI  / 40){
    console.log(i);
    setQuick(spiralImage, x + cos(i) * size, y + sin(i) * size, c);
  }
}

function getQuick(img, x, y) {
  const i = (y * img.width + x) * 4;
  return [
    img.pixels[i],
    img.pixels[i + 1],
    img.pixels[i + 2],
    img.pixels[i + 3],
  ];
}

function setQuick(img, x, y, c) {
  const i = (y * img.width + x) * 4;
  img.pixels[i + 0] = c[0];
  img.pixels[i + 1] = c[1];
  img.pixels[i + 2] = c[2];
  img.pixels[i + 3] = 255; // c[3];
}

