// require https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js
// this is based on code from compform.net: https://compform.net/toc_mess.js

/// main orange
// 244, 133, 53
// background
// 248, 206, 166
// bottom color
// 233, 172, 127


const target_size = 20;
const debug = true;
const THRESH = 3;

// globals
let count = 0;
let grid_cols = 0;
let grid_rows = 0;
let grid_width = 0;
let grid_height = 0;
let grid = [];
let cell_history = [];
let dir_history = [];
let current_cell = {x: 0, y: 0};

let frameCountStop; 

function setup() {
  createCanvas(800, 800);
  blendMode(MULTIPLY);
  noStroke();
  resize();
  background(248, 206, 166);
}

function draw() {
  step();
}


function resize() {
  // find best fit for grid
  // divide the page into columns and rows
  grid_cols = floor(width / target_size);
  grid_rows = floor(height / target_size);

  // get the width of a column / row
  grid_width = width / grid_cols;
  grid_height = height / grid_rows;

  // start in lower right corner
  current_cell.x = 1; // random(1, grid_cols - 1);
  current_cell.y = 1; // random(1, grid_rows - 1);

  // initialize 2d array
  for (var i = 0; i < grid_cols; i++) {
    grid[i] = [];
    for (var j = 0; j < grid_rows; j++) {
      grid[i][j] = false;
    }
  }

  if(debug){
    console.log(grid_rows, grid_cols, grid);
  }
}

function reset(random) {
  // initialize 2d array
  for (var i = 0; i < grid_cols; i++) {
    for (var j = 0; j < grid_rows; j++) {
      grid[i][j] = false;
    }
  }

  if(random == true){
    current_cell.x = 1; // random(1, grid_cols - 1);
    current_cell.y = 1; // random(1, grid_rows - 1);
  }
}

function noiseShuffle(x, array){
  for (let i = array.length - 1; i > 0; i--) {
    const j = floor(map(noise(x * i), 0, 1, 0, array.length));
    // console.log(j);
    const temp = array[i];
    array[i] = array[j];
    array[j] = temp;
  }
  return array;
}

function step() {
  let x = 0;
  let y = 0;
  let found_open_cell = false;

  // look at adjacent cells in random order, try to find open cell
  // direction is biased right
  let frameCountHolder;
  if(count == 1){
    frameCountHolder = frameCount % frameCountStop;
  } else {
    frameCountHolder = frameCount;
  }
  let directions = noiseShuffle(frameCountHolder, ["up", "down", "left", "right"]);

  // pick an item from the direction array (once for each element)
  for(const dir of directions){

    // get the x and y of the current cell
    x = current_cell.x;
    y = current_cell.y; 

    // increment the coordinates depending on direction
    // check if the current cell and the one in front of it are open
    if (dir == "up") {
      y--;
    } else if (dir == "right") {
      x++;
    } else if (dir == "down") {
      y++;
    } else if (dir == "left") {
      x--;
    }

    if(checkCell(x, y, dir)){
      found_open_cell = true;
      break;
    }
  }

  // if those coordinates in the grid are not filled then we found a cell
  // draw the new cell
  if (found_open_cell) {
    // move into cell
    current_cell.x = x;
    current_cell.y = y;

    // mark cell as full
    grid[current_cell.x][current_cell.y] = true;

    // add new cell to history
    cell_history.push({
      x: current_cell.x,
      y: current_cell.y,
    });

    // draw new cell
    if(count == 1){
      fill(233, 172, 127, 100);
      rect(current_cell.x * grid_width + target_size / 3,
           current_cell.y * grid_height + target_size / 3,
           grid_width ,
           grid_height);

    }else{
      fill(244, 133, 53, 200);
      rect(current_cell.x * grid_width,
           current_cell.y * grid_height,
           grid_width ,
           grid_height);
    }

  // dead end case
  } else {
    // no adjacent cell is empty so we hit a dead end
    if (cell_history.length) {
      // back up to the previous cell and try again
      current_cell = cell_history.pop();
    } else {
      console.log("finished " + count + " iterations");
      // all done
      count++;
      if(count == 2){
        noLoop();
      } else {
        reset(true);
        frameCountStop = frameCount;
      }
    }
  }
}

