let circleSize = 0.7;
let circleD = 0;

function setup() {
  createCanvas(windowWidth, windowHeight);
  pixelDensity(1);
}

function draw() {
  resizeCanvas(windowWidth, windowHeight);
  background(220);

  circleD = circleSize * width;

  loadPixels();

  for (let y = 0; y < height; y++) {
    for (let x = 0; x < width; x++) {
      let index = (x+y*width)*4;

      if (dist(width/2, height/2, x, y) > circleD*0.5 || dist(width/2, height/2, x, y) > circleD*0.5
      ) {

        let r = map(cos(y) * noise(x), -1, 1, 0, 75) * noise(x);
        let g = map(tan(y) * noise(x*y), -1, 1, 0, 15) * noise(x*y);
        let b = map(sin(y) * noise(y), -1, 1, 0, 100) * noise(x);

        pixels[index] = r;
        pixels[index+1] = g;
        pixels[index+2] = b;
        pixels[index+3] = 255;      
      }
      else {
        pixels[index] = map(x, 0, width, 150, 255);
        pixels[index+1] = 0;
        pixels[index+2] = x*y;
        pixels[index+3] = x * noise(x*y);
      }

    }
  }

  updatePixels();
}

