      path.strokeColor = path.fillColor ;
    };
  }

  function createBlob(center, maxRadius, points) {
    var path = new Path();
    path.closed = true;
    for (var i = 0; i < points; i++) {
      var delta = new Point({
        length: (maxRadius * 0.5) + (Math.random() * maxRadius * 0.5),
        angle: (360 / points) * i
      });
      path.add(center + delta);
    }
    path.smooth();
    return path;
