// draw a road from right to left
function createCoast(){
  let xCur = 0;
  let yCur = 0;
  let xDist = 0;
  let yDist = 0;

  for (var i = 0; i < grid_cols; i++) {
    coast_cells[i] = [];
    for (var j = 0; j < grid_rows; j++) {
      coast_cells[i][j] = false;
    }
  }

  for (var i = 0; i < grid_cols; i++) {
    for (var j = 0; j < grid_rows; j++) {
      // make sure the treasure and the road ends up on the map
      if(noise(i * 0.08, j * 0.08) > 0.4 || 
        roadA_cells[i][j] == true || 
        roadB_cells[i][j]){
        coast_cells[i][j] = true;
      }
    }
  }
}



// draw a road from right to left
function createRoads(){
  let xCur = 0;
  let yCur = 0;
  let xDist = 0;
  let yDist = 0;
  let updown = true;

  for (var i = 0; i < grid_cols; i++) {
    roadA_cells[i] = [];
    roadB_cells[i] = [];
    for (var j = 0; j < grid_rows; j++) {
      roadA_cells[i][j] = false;
      roadB_cells[i][j] = false;
    }
  }

 //  if(updown){
    while(xCur < grid_cols && yCur < grid_rows){
      // console.log(xCur, yCur, xDist, yDist);

      let dir = random([true, false]);

      // move to the right
