        // pick a distance
        xDist = floor(random(0, grid_cols / 8));
        // make sure we're not exceeding the page
        while(xCur + xDist > grid_cols){
          xDist--;
        }
        // move in that direction
        for(let i = 0; i < xDist; i++){
          roadA_cells[i + xCur][yCur] = true;
        }
        // increment
        xCur = xCur + xDist;
      }else{
        yDist = floor(random(0, grid_rows / 8));
        while(yCur + yDist > grid_rows){
